INSERT INTO `b_iblock_element_prop_m10` VALUES (1,24,72,'Christina',NULL,0.0000,''),(2,24,72,'История компании',NULL,0.0000,''),(3,24,72,'Производство',NULL,0.0000,''),(4,24,87,'a:2:{s:4:\"TEXT\";s:789:\"Компания Christina (Израиль), основанная в 1982 году, на сегодняшний день является одной из крупнейших в мире фирм по производству продукции, предназначенной для специалистов индустрии красоты. Имея за плечами тридцатилетний стаж успешной работы, Christina не перестает приятно удивлять своих клиентов смелыми экспериментами, революционными решениями и инновациями, представляя новые уникальные косметические препараты по уходу за кожей.\";s:4:\"TYPE\";s:4:\"HTML\";}',NULL,0.0000,''),(5,24,87,'a:2:{s:4:\"TEXT\";s:527:\"Изначально марка Christina представляла собой небольшое семейное предприятие, которое, спустя годы, благодаря введению инноваций и по-настоящему революционных технологий, превратилось в одну из крупнейших в мире компаний по производству косметических средств по уходу за кожей лица и тела.\";s:4:\"TYPE\";s:4:\"HTML\";}',NULL,0.0000,''),(6,24,87,'a:2:{s:4:\"TEXT\";s:789:\"Компания Christina (Израиль), основанная в 1982 году, на сегодняшний день является одной из крупнейших в мире фирм по производству продукции, предназначенной для специалистов индустрии красоты. Имея за плечами тридцатилетний стаж успешной работы, Christina не перестает приятно удивлять своих клиентов смелыми экспериментами, революционными решениями и инновациями, представляя новые уникальные косметические препараты по уходу за кожей.\";s:4:\"TYPE\";s:4:\"HTML\";}',NULL,0.0000,''),(7,24,87,'a:2:{s:4:\"TEXT\";s:789:\"Компания Christina (Израиль), основанная в 1982 году, на сегодняшний день является одной из крупнейших в мире фирм по производству продукции, предназначенной для специалистов индустрии красоты. Имея за плечами тридцатилетний стаж успешной работы, Christina не перестает приятно удивлять своих клиентов смелыми экспериментами, революционными решениями и инновациями, представляя нов