INSERT INTO `b_search_content_text` VALUES (1,'2e589b8811433d2c621ac35e7247a9e8','PROCLEAR NIGHT CREAM REPAIR BRIGHTEN\r\nРЕВОЛЮЦИОННЫЙ ВОССТАНАВЛИВАЮЩИЙ НОЧНОЙ КРЕМ\r\n'),(2,'2e589b8811433d2c621ac35e7247a9e8','PROCLEAR NIGHT CREAM REPAIR BRIGHTEN\r\nРЕВОЛЮЦИОННЫЙ ВОССТАНАВЛИВАЮЩИЙ НОЧНОЙ КРЕМ\r\n'),(3,'2e589b8811433d2c621ac35e7247a9e8','PROCLEAR NIGHT CREAM REPAIR BRIGHTEN\r\nРЕВОЛЮЦИОННЫЙ ВОССТАНАВЛИВАЮЩИЙ НОЧНОЙ КРЕМ\r\n'),(4,'3ee4fd3682dd9dbdcafb6efb2f9da7af','1\r\n\r\n'),(5,'63e312ea09a5501e2d3b41e2fa2849df','2\r\n\r\n'),(6,'f618996faf23dd379fe6e5ff4a4dda55','УМНЫЕ ТРАНСПОРТНЫЕ СИСТЕМЫ\r\nК ТРАНСПОРТНЫМ СИСТЕМАМ ОРГАНИЗМА ОТНОСЯТСЯ КРОВЕНОСНАЯ И ЛИМФАТИЧЕСКАЯ СИСТЕМЫ. ОНИ СВЯЗАНЫ И ДОПОЛНЯЮТ ОДНА ДРУГУЮ\r\n'),(7,'893ae47029f0876a38c4c5b2f82cca75','ДВУХСЛОЙНЫЕ ЛИПОСОМЫ\r\nЛИПОСОМЫ ПРЕДСТАВЛЯЮТ СОБОЙ НАНОРАЗМЕРНЫЕ ЧАСТИЦЫ ИЗ ФОСФОЛИПИДОВ И ХОЛЕСТЕРОЛОВ, ВСЛЕДСТВИЕ ЧЕГО ИХ ИНОГДА НАЗЫВАЮТ «ЖИРОВЫМИ ПУЗЫРЬКАМИ»\r\n'),(8,'f4da8ebb9e0baae8f1b2b495edcfeb34','МИКРО-ИНКАПСУЛЯЦИЯ\r\nПРОЦЕСС ВКЛЮЧЕНИЯ КАКОЙ-ЛИБО СУБСТАНЦИИ (ВЕЩЕСТВА) В МАЛЕНЬКИЕ КАПСУЛКИ, ИЗ КОТОРЫХ ЭТИ СУБСТАНЦИИ МОГУТ ОСВОБОЖДАТЬСЯ В ДАЛЬНЕЙШЕМ С ПОМОЩЬЮ ПОВЫШЕНИЯ ТЕМПЕРАТУРЫ\r\n'),(9,'4347a04f8a2259ea87c10ffad4234013','БИОМЕТРИЧЕСКИЕ ПЕПТИДЫ\r\nСЕМЕЙСТВО ВЕЩЕСТВ, МОЛЕКУЛЫ КОТОРЫХ ПОСТРОЕНЫ ИЗ ДВУХ И БОЛЕЕ ОСТАТКОВ АМИНОКИСЛОТ, СОЕДИНЁННЫХ В ЦЕПЬ ПЕПТИДНЫМИ (АМИДНЫМИ) СВЯЗЯМИ\r\n'),(10,'3c2f8ee1ef157edd28d3d822faf3bc28','КРИСТИНА М. ЗЕХАВИ: «ЧЕМ БОЛЬШЕ ВЫ ЛЮБИТЕ, ТЕМ ВЫ СЧАСТЛИВЕЕ».\r\nНАВЕРНО, УЖЕ НЕ ОСТАЛОСЬ НИ В ОДНОМ ЯЗЫКЕ МИРА СЛОВ, НЕ СКАЗАННЫХ О ЛЮБВИ\r\n\rЧАСТО В ГОНКЕ ЗА БЫСТРЫМ РЕЗУЛЬТАТОМ ВРАЧИ-КОСМЕТОЛОГИ ЗАБЫВАЮТ ПРО ОСНОВЫ КОСМЕТОЛОГИЧЕСКОГО ЛЕЧЕНИЯ — МЕТОДИКИ, ДАЮЩИЕ КОЖЕ ЗДОРОВЬЕ.\rЕСЛИ ПРЕДСТАВИТЬ КОСМЕТОЛОГИЧЕСКОЕ ЛЕЧЕНИЕ КАК ДОМ, ТО ФУНДАМЕНТОМ ЕГО БУДЕТ ПРАВИЛЬНЫЙ ДОМАШНИЙ УХОД. ИМЕННО ПРАВИЛЬНО ПОДОБРАННАЯ ДОМАШНЯЯ КОСМЕТИКА, А ИМЕННО: УМЫВАЮЩЕЕ СРЕДСТВО, КРЕМ ДНЕВНОЙ, КРЕМ НОЧНОЙ, СЫВОРОТКА, КРЕМ ВОКРУГ ГЛАЗ И ЭФФЕКТИВНОЕ ТОНИРУЮЩЕЕ СРЕДСТВО — ЗАЛОГ УСПЕШНОЙ РАБОТЫ. ВАЖНО ЧТОБЫ ВСЕ ЭТИ СРЕДСТВА БЫЛИ НАЗНАЧЕНЫ ВРАЧОМ-КОСМЕТОЛОГОМ ИЗ ПРОФЕССИОНАЛЬНОЙ СЕРИИ КОСМЕТОЛОГИЧЕСКИХ СРЕДСТВ.\r\n'),(11,'f82af7acd7a4ad2ee0ece6d1e27d4104','2 СОВЕТА, ЕСЛИ ГУБЫ СОХНУТ НА МОРОЗЕ\r\nЗНАКОМАЯ ИСТОРИЯ? ИДЕТЕ ВЕТРЕНЫМ ИЛИ МОРОЗНЫМ ДНЁМ ПО УЛИЦЕ\r\n'),(12,'226d4d1361931d39e0c618c75930a611','УВЛАЖНЯЮЩИЙ КРЕМ ДЛЯ ЛИЦА В МОРОЗ:ПОЛЬЗА ИЛИ ВРЕД\r\nМНЕНИЕ, ЧТО НАНОСИТЬ УВЛАЖНЯЮЩИЙ КРЕМ В МОРОЗ РАВНОСИЛЬНО\r\n'),(13,'4c43dceee160579a29283995e9925335','ПРЕПАРАТЫ CHRISTINA: СОЮЗ НАУКИ И ПРИРОДЫ4\r\nВ ПЕРВЫЙ ДЕНЬ ВЕСНЫ МЫ ХОТИМ БЫТЬ ПЕРВЫМИ, КТО ПОЗДРАВИТ ВАС С ЭТИМ ДОЛГОЖДАННЫМ СОБЫТИЕМ.\r\n'),(14,'37806c66dfc40eaaa7c36383b61f7b5a','ПРЕПАРАТЫ CHRISTINA: СОЮЗ НАУКИ И ПРИРОДЫ\r\nВ ПЕРВЫЙ ДЕНЬ ВЕСНЫ МЫ ХОТИМ БЫТЬ ПЕРВЫМИ, КТО ПОЗДРАВИТ ВАС С ЭТИМ ДОЛГОЖДАННЫМ СОБЫТИЕМ.\r\n'),(15,'37806c66dfc40eaaa7c36383b61f7b5a','ПРЕПАРАТЫ CHRISTINA: СОЮЗ НАУКИ И ПРИРОДЫ\r\nВ ПЕРВЫЙ ДЕНЬ ВЕСНЫ МЫ ХОТИМ БЫТЬ ПЕРВЫМИ, КТО ПОЗДРАВИТ ВАС С ЭТИМ ДОЛГОЖДАННЫМ СОБЫТИЕМ.\r\n'),(16,'30ec05decec5cb3413263e12a43544b6','ПРЕПАРАТЫ CHRISTINA: СОЮЗ НАУКИ И ПРИРОДЫ3\r\nВ ПЕРВЫЙ ДЕНЬ ВЕСНЫ МЫ ХОТИМ БЫТЬ ПЕРВЫМИ, КТО ПОЗДРАВИТ ВАС С ЭТИМ ДОЛГОЖДАННЫМ СОБЫТИЕМ.\r\n\rВЫ ЯВЛЯЕТЕСЬ КЛИЕНТОМ ИНТЕРНЕТ-МАГАЗИНА ПРОФЕССИОНАЛЬНОЙ КОСМЕТИКИ CHRISTINA? ЗНАЧИТ ВАС ЗАИНТЕРЕСУЕТ НАШЕ НОВОЕ ПРЕДЛОЖЕНИЕ! ПРИВЕДИТЕ К НАМ СВОЕГО ДРУГА И ПОЛУЧИТЕ БОНУС ЗА ЕГО ПОКУПКУ! ПРИ ЭТОМ ВАШ ДРУГ ПОЛУЧИТ СКИДКУ НА СВОЙ ПЕРВЫЙ ЗАКАЗ.\rНАШ ИНТЕРНЕТ-МАГАЗИН ПРОФЕССИОНАЛЬНОЙ КОСМЕТИКИ CHRISTINA ПРЕДОСТАВЛЯЕТ БОНУСЫ КАЖДОМУ, КТО ПРИГЛАСИТ К НАМ НОВОГО ПОКУПАТЕЛЯ! НЕ УПУСТИТЕ ШАНС ВОСПОЛЬЗОВАТЬСЯ ПРЕДЛОЖЕНИЕМ!\rКАК ПРИНЯТЬ УЧАСТИЕ В АКЦИИ?\rСДЕЛАЙТЕ ЗАКАЗ НА НАШЕМ САЙТЕ \rWWW.CHRISTINACOSMETICS.RU\rПОСЛЕ ОПЛАТЫ ЗАКАЗА ВЫ ПОЛУЧИТЕ СООБЩЕНИЕ С ПРОМОКОДО\rПОДЕЛИТЕСЬ ЭТИМ ПРОМОКОДОМ С ДРУГОМ\rВАШ ДРУГ ПОЛУЧИТ СКИДКУ 10% ПРИ ЗАКАЗЕ НА СУММУ ОТ 5000 ₽ \rЗА ПОКУПКУ ДРУГА ВЫ ПОЛУЧИТЕ БОНУС В РАЗМЕРЕ 500 ₽\rУСЛОВИЯ АКЦИИ «ПОДЕЛИСЬ С ДРУГОМ ПРОМОКОДОМ – ПОЛУЧИ БОНУС»\rПРОМОКОД ДЕЙСТВУЕТ В ТЕЧЕНИЕ 15 ДНЕЙ С ДАТЫ ПОЛУЧЕНИЯ\rСКИДКА 10 % ПО ПРОМОКОДУ ДЕЙСТВУ